mapper.select:
SELECT

FROM
    `comment`
WHERE
    entity_type = #{entityType}
  AND entity_id = #{entityId}
  AND `status` = 0
ORDER BY
    create_time DESC

    LIMIT #{offset}, #{limit};

SELECT
    COUNT( id )
FROM
    `comment`
WHERE
    entity_type = #{entityType}
  AND entity_id = #{entityId}
  AND `status` = 0;
